import React, { useContext } from 'react'

import {CurrentUserContext} from 'contexts/currentUser'

import useFetch from 'hooks/useFetch'

const CommentItem = ({ body, author, createdAt, handleDelete, id }) => {
  const [currentUserState] = useContext(CurrentUserContext)
  const {currentUser: user} = currentUserState
  return (
    <div className="card">
      <div className="card-block">
        <p className="card-text">{body}</p>
      </div>
      <div className="card-footer">
        <a className="comment-author" href={`/profile/${author.username}`}>
          <img className="comment-author-img" src="https://static.productionready.io/images/smiley-cyrus.jpg"/>
        </a> &nbsp;
        <a className="comment-author" href={`/profile/${author.username}`}> {author.username} </a>
        <span className="date-posted">{createdAt} </span>
        {(user.username === author.username) && (
          <span className="mod-options" hidden="" onClick={() => {
            handleDelete(id)
          }}><i className="ion-trash-a"></i>
          </span>  
        )}
      </div>
    </div>
  )
}

export default CommentItem
